INSERT INTO `videos` VALUES (1,'68016','tx6MXTo49Wk.jpg','IELTS - You DON\'T NEED TEACHERS!',NULL,'[{\"id\":\"29635584\",\"caption_id\":\"137474\",\"seq\":\"1\",\"text\":\"Hello. Welcome to IELTS KILLER\nThe video series which gives you the best\",\"translate_comment\":null,\"start\":\"10.36\",\"dur\":\"4.649\",\"is_scores\":null,\"created_at\":\"1532595179\",\"updated_at\":null},{\"id\":\"29635585\",\"caption_id\":\"137474\",\"seq\":\"2\",\"text\":\"ideas to use in your important IELTS Writing\nEssay\",\"translate_comment\":null,\"start\":\"15.009\",\"dur\":\"3.481\",\"is_scores\":null,\"created_at\":\"1532595179\",\"updated_at\":null},{\"id\":\"29635586\",\"caption_id\":\"137474\",\"seq\":\"3\",\"text\":\"Letu2019s look at todayu2019s topic\nSome people think computers & the Internet\",\"translate_comment\":null,\"start\":\"18.49\",\"dur\":\"6.06\",\"is_scores\":null,\"created_at\":\"1532595179\",\"updated_at\":null},{\"id\":\"29635587\",\"caption_id\":\"137474\",\"seq\":\"4\",\"text\":\"are important in childrenu2019s study\nBut others think students can learn more effectively\",\"translate_comment\":null,\"start\":\"24.55\",\"dur\":\"5.12\",\"is_scores\":null,\"created_at\":\"1532595179\",\"updated_at\":null},{\"id\":\"29635588\",\"caption_id\":\"137474\",\"seq\":\"5\",\"text\":\"in schools & with teachers\nDiscuss both views & give your opinion\",\"translate_comment\":null,\"start\":\"29.67\",\"dur\":\"4.99\",\"is_scores\":null,\"created_at\":\"1532595179\",\"updated_at\":null},{\"id\":\"29635589\",\"caption_id\":\"137474\",\"seq\":\"6\",\"text\":\"So which side are you on?\nThe computer & Internet\",\"translate_comment\":null,\"start\":\"34.66\",\"dur\":\"5.059\",\"is_scores\":null,\"created_at\":\"1532595179\",\"updated_at\":null},{\"id\":\"29635590\",\"caption_id\":\"137474\",\"seq\":\"7\",\"text\":\"Or teachers? The real ones\",\"translate_comment\":null,\"start\":\"39.719\",\"dur\":\"3.631\",\"is_scores\":null,\"created_at\":\"1532595179\",\"updated_at\":n